import RecipeCard from '../components/RecipeCard'
import React from 'react'

class RecipeContainer extends React.Component {

    state = {
        recipes: [],
        search: ""
    }

    createRecipeCards() {
        const recipes = this.state.recipes
        return recipes.map(recipe => <RecipeCard recipe={recipe} id={recipe.id} name={recipe.name} image={recipe.img_url} description={recipe.description}/>)
    }

    componentDidMount() {
        const url = 'http://127.0.0.1:3000/recipes'

        const configObj = {
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json"
            }
        }

        fetch(url, configObj)
        .then(r => r.json())
        .then(json => {
            console.log(json)
            this.setState({
                recipes: json
            })
        })
    }

    render() {
        return (
            <div id="recipe-container">
                {this.createRecipeCards()}
            </div>
        )
    }
}

export default RecipeContainer
