import RecipeCard from '../components/RecipeCard'
import React from 'react'

class RecipeContainer extends React.Component {

    state = {
        recipes: [],
        search: ""
    }

    createRecipeCards() {
        const recipes = this.state.recipes
        console.log(recipes)
        return recipes.map(recipe => <RecipeCard recipe={recipe} id={recipe.id} name={recipe.name} image={recipe.img_url} description={recipe.description}/>)
    }

    componentDidMount() {
        const url = 'http://localhost:3000/recipes'
        const url2 = 'https://www.themealdb.com/api/json/v2/9973533/randomselection.php'

        // const configObj = {
        //     headers: {
        //         "Content-Type": "application/json",
        //         Accept: "application/json"
        //     }
        // }

        fetch(url2)
        .then(r => r.json())
        .then(json => {
            console.log(json)
            this.setState({
                recipes: json["m,e"]
            })
        })
    }

    render() {
        return (
            <div id="recipe-container">
                {this.createRecipeCards()}
            </div>
        )
    }
}

export default RecipeContainer
